pragma solidity ^0.8.17;

import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "./BCTypes.sol";
import "./BCEvents.sol";
import "./BCUtils.sol";

interface CharInterface {
    function characters(
        uint _id
    ) external view returns (BCTypes.Character memory);

    function incrementCloneNumber(uint _charId) external;
}

interface MapInterface {
    function createMap() external returns (uint);

    function extGetBoard(
        uint _mapId
    ) external view returns (BCTypes.GameTile[][] memory);

    function extGetGameTile(
        uint _mapId,
        uint _row,
        uint _col
    ) external view returns (BCTypes.GameTile memory);

    function getDoor(
        uint _mapId,
        uint _row,
        uint _col,
        uint _direction
    ) external view returns (BCTypes.Door memory);

    function setDoorFromPos(
        uint _mapId,
        uint _row,
        uint _col,
        uint _direction,
        BCTypes.DoorStatus _status,
        uint _strength
    ) external;

    function resetOpenDoors(uint _mapId) external;

    function setLooted(uint _mapId, uint _row, uint _col) external;

    function extGetRoom(
        uint _mapId,
        uint _row,
        uint _col
    ) external view returns (BCTypes.RoomTile memory);

    function extGetRoomId(
        uint _mapId,
        uint row,
        uint col
    ) external view returns (uint);
}

interface GamesInterface {
    function extGetGame(uint _id) external view returns (BCTypes.Game memory);

    function extMovePlayer(
        uint _mapId,
        BCTypes.Position memory _position,
        uint _playerId
    ) external;

    function endPlayerAction(uint _gameId, uint _playerId, bool _free) external;

    function leaveGame(
        uint _gameId,
        uint _playerId,
        uint _charId,
        uint _currentPlayerTurnIndex
    ) external;

    function playerAttack(
        uint _gameId,
        uint _denizenId,
        uint _playerId,
        uint _playerAttackRating, // Can be melee or ranged, selected in Actions contract
        BCTypes.Position memory _playerPosition
    ) external;
}

interface ItemsInterface {
    function lootItem(
        uint _holdingPlayerId,
        address _holderAddress,
        uint _gameId
    ) external payable;

    function spawnWorldItems(
        uint _gameId,
        uint[] memory _itemIds,
        uint _row,
        uint _col,
        uint _formerOwnerId
    ) external;

    function pickupWorldItems(
        uint _playerId,
        uint[] calldata _itemIds,
        address _newOwnerAddress
    ) external;

    function getItemIDsByPlayer(
        uint _playerID
    ) external view returns (uint[] memory);

    function getWorldItems(
        uint _gameId
    ) external view returns (BCTypes.Item[] memory);
}

interface EventsInterface {
    function extGetRoomEvent(
        uint _id
    ) external view returns (BCEvents.BCEvent memory);

    function extGetCardEvent(
        uint _id
    ) external view returns (BCEvents.BCEvent memory);

    function extGetEffects(
        bool _roomEvent,
        uint _id
    ) external view returns (BCEvents.Effect[][] memory);

    function extGetTraitModForEffect(
        bool _roomEvent,
        uint _id,
        uint _effect
    ) external view returns (BCTypes.Traits memory);
}

interface PlayersInterface {
    function grantDataToPlayer(uint _playerId, uint8 amount) external;

    function extApplyEffect(
        uint _playerId,
        uint _gameId,
        uint _mapId,
        BCTypes.Position memory _position,
        uint _eventId,
        uint _effectNum
    ) external;

    function extGetPlayer(
        uint _id
    ) external view returns (BCTypes.Player memory);

    function incrementNumItemsHeld(uint _playerId, uint _num) external;

    function decrementNumItemsHeld(uint _playerId, uint _num) external;

    function updatePlayerTraits(uint _charId, uint _playerId) external;
}

contract Actions is Ownable, BCUtils {
    using EnumerableSet for EnumerableSet.UintSet;

    CharInterface public chars;
    MapInterface public maps;
    GamesInterface public games;
    ItemsInterface public items;
    EventsInterface public bcEvents;
    PlayersInterface public players;

    address public charsAddress;

    function setContractAddresses(
        address _charAddress,
        address _utilsAddress, // TODO: Remove
        address _mapsAddress,
        address _gamesAddress,
        address _itemsAddress,
        address _eventsAddress,
        address _playersAddress
    ) public onlyOwner {
        chars = CharInterface(_charAddress);
        maps = MapInterface(_mapsAddress);
        games = GamesInterface(_gamesAddress);
        items = ItemsInterface(_itemsAddress);
        bcEvents = EventsInterface(_eventsAddress);
        players = PlayersInterface(_playersAddress);
    }

    // TODO: Probably add map and all players to get update in FE
    event ActionCompleteEvent(
        uint gameId,
        BCTypes.Game game,
        uint playerId,
        BCTypes.Player player,
        BCTypes.Action action
    );

    uint constant DEFAULT_ACTIONS_NUM = 3; // TODO: Duped in BCGames.sol

    uint constant START_ROOM_ID = 2; // TODO: Duplicated in BCMaps.sol
    uint8 constant UNDISCOVERED_ROOM = 1;

    uint constant ITEM_COST = 100 gwei; // TODO: Duplicated in UniversalInventory

    function _validateItemsHeld(
        uint[] memory _ids,
        uint _playerId
    ) internal view returns (bool) {
        uint[] memory heldItemIds = items.getItemIDsByPlayer(_playerId);

        // o(n^2) but n shouldn't ever be bigger than 5
        for (uint i = 0; i < _ids.length; i++) {
            bool currentFound = false;
            for (uint k = 0; k < heldItemIds.length; k++) {
                if (_ids[i] == heldItemIds[k]) {
                    currentFound = true;
                    break;
                }
            }
            if (currentFound == false) {
                // We didn't find an item, generate an error
                return false;
            }
        }
        return true;
    }

    // TODO: Decompose
    function doAction(
        uint _gameId,
        uint _playerId,
        BCTypes.Action _action,
        BCTypes.Followthrough _followthrough,
        uint _1stDirection, // NSEW 4==in room
        uint _2ndDirection, // NSEW 4==in room
        uint[] memory _ids
    ) public payable {
        // Universal requirements for all actions
        // TODO: CRITICAL -> Make sure game exists and id is correct!
        // TODO: Player must not be dead!
        // TODO: Optimize:  Since using memory now, explore just getting what is needed
        // TODO: WHY DO I NEED EXT FOR PLAYER AND GAME BUT NOT CHAR?????
        BCTypes.Player memory currentPlayer = players.extGetPlayer(_playerId);
        BCTypes.Game memory currentGame = games.extGetGame(_gameId); // Games are huge, get less
        // BCTypes.Character memory currentChar = chars.characters(currentPlayer.characterId);
        require(currentGame.eventNumber == 0, "Must resolve event first");
        require(currentGame.denizenTurn == false, "Finish denizen turn");
        require(currentGame.active, "Game not active");
        require(msg.sender == currentPlayer.owner, "Sender not player owner");
        require(
            currentPlayer.actionsTaken < DEFAULT_ACTIONS_NUM,
            "No actions left"
        ); // TODO: Confirm this won't ever trigger // TODO: Get effects and items
        // TODO: Validate if the below also sufficiently confirms the player is in this game
        require(
            _playerId ==
                currentGame.playerIndexes[currentGame.currentPlayerTurnIndex],
            "Not your turn"
        );

        bool free = false;
        // TODO: Add respawn action for living or dead players
        // TODO: Check for value here?
        if (_action == BCTypes.Action.PASS) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            // Do nothing
        } else if (_action == BCTypes.Action.MOVE) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            // Main move
            BCTypes.Door memory firstRefDoor = maps.getDoor(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col,
                _1stDirection
            );
            require(
                firstRefDoor.status == BCTypes.DoorStatus.OPEN ||
                    firstRefDoor.status == BCTypes.DoorStatus.BREACHED ||
                    firstRefDoor.vsHack == 0, // TODO: Confirm no edge cases
                "1st Door not open/breached/unlocked"
            );

            if (firstRefDoor.status == BCTypes.DoorStatus.CLOSED) {
                maps.setDoorFromPos(
                    currentGame.mapId,
                    currentPlayer.position.row,
                    currentPlayer.position.col,
                    _1stDirection,
                    BCTypes.DoorStatus.OPEN,
                    0
                );
            }

            BCTypes.Position memory targetRoomPos = getNeighborRoom(
                _1stDirection,
                currentPlayer.position
            );

            // Followthrough
            if (_followthrough == BCTypes.Followthrough.MOVE) {
                BCTypes.Position memory secondRoom = getNeighborRoom(
                    _2ndDirection,
                    targetRoomPos
                );
                BCTypes.Door memory secondRefDoor = maps.getDoor(
                    currentGame.mapId,
                    targetRoomPos.row,
                    targetRoomPos.col,
                    _2ndDirection
                );
                require(
                    secondRefDoor.status == BCTypes.DoorStatus.OPEN ||
                        secondRefDoor.status == BCTypes.DoorStatus.BREACHED ||
                        secondRefDoor.vsHack == 0, // TODO: Confirm no edge cases
                    "2nd Door not open/breached/unlocked"
                );

                BCTypes.GameTile[][] memory currentMap = maps.extGetBoard(
                    currentGame.mapId
                );
                require(
                    currentMap[targetRoomPos.row][targetRoomPos.col].roomId >
                        START_ROOM_ID,
                    "Room not explored"
                );

                if (secondRefDoor.status == BCTypes.DoorStatus.CLOSED) {
                    maps.setDoorFromPos(
                        currentGame.mapId,
                        targetRoomPos.row,
                        targetRoomPos.col,
                        _2ndDirection,
                        BCTypes.DoorStatus.OPEN,
                        0
                    );
                }
                targetRoomPos = secondRoom;
            }
            games.extMovePlayer(_gameId, targetRoomPos, _playerId);
        } else if (_action == BCTypes.Action.HACK) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            BCTypes.Door memory firstRefDoor = maps.getDoor(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col,
                _1stDirection
            );
            require(
                firstRefDoor.status == BCTypes.DoorStatus.CLOSED,
                "Door not closed"
            );

            if (
                resolveChallenge(
                    _gameId,
                    uint8(currentPlayer.currentTraits.hack),
                    firstRefDoor.vsHack
                )
            ) {
                maps.setDoorFromPos(
                    currentGame.mapId,
                    currentPlayer.position.row,
                    currentPlayer.position.col,
                    _1stDirection,
                    BCTypes.DoorStatus.OPEN,
                    0
                );
                // TODO: DRY - consolidate with breach
                if (_followthrough == BCTypes.Followthrough.MOVE) {
                    games.extMovePlayer(
                        _gameId,
                        getNeighborRoom(_1stDirection, currentPlayer.position),
                        _playerId
                    );
                }
                // TODO: Handle shoot through doorway
                // TODO: Handle grenade through doorway
            }
        } else if (_action == BCTypes.Action.BREACH) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            BCTypes.Door memory firstRefDoor = maps.getDoor(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col,
                _1stDirection
            );
            require(
                firstRefDoor.status == BCTypes.DoorStatus.CLOSED,
                "Door not closed"
            );
            if (
                resolveChallenge(
                    _gameId,
                    uint8(currentPlayer.currentTraits.breach),
                    firstRefDoor.vsBreach
                )
            ) {
                maps.setDoorFromPos(
                    currentGame.mapId,
                    currentPlayer.position.row,
                    currentPlayer.position.col,
                    _1stDirection,
                    BCTypes.DoorStatus.BREACHED,
                    0
                );
                // TODO: DRY - consolidate with hack
                if (_followthrough == BCTypes.Followthrough.MOVE) {
                    games.extMovePlayer(
                        _gameId,
                        getNeighborRoom(_1stDirection, currentPlayer.position),
                        _playerId
                    );
                }
                // TODO: Handle shoot through doorway
                // TODO: Handle grenade through doorway
                // TODO: Handle breaching into space
            }
        } else if (_action == BCTypes.Action.LOOT) {
            // TODO: Should cost an action if an enemy is present
            free = true;
            BCTypes.GameTile memory currentTile = maps.extGetGameTile(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col
            );
            require(currentTile.looted == false, "Room already looted");

            BCTypes.RoomTile memory currentRoomTile = maps.extGetRoom(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col
            );
            require(
                (currentRoomTile.numData > 0 || currentRoomTile.numItems > 0),
                "Can't loot empty room"
            );
            require(
                msg.value == currentRoomTile.numItems * ITEM_COST,
                "Wrong payment in actions"
            );
            maps.setLooted(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col
            );

            players.grantDataToPlayer(_playerId, currentRoomTile.numData);

            for (uint i = 0; i < currentRoomTile.numItems; i++) {
                // TODO: Confirm this is ok because I control destination address
                items.lootItem{value: 100 gwei}(
                    _playerId,
                    currentPlayer.owner,
                    currentPlayer.gameId
                );
            }
            players.incrementNumItemsHeld(_playerId, currentRoomTile.numItems);
            players.updatePlayerTraits(currentPlayer.characterId, _playerId);
        } else if (_action == BCTypes.Action.USE_ROOM) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            BCTypes.RoomTile memory currentRoom = maps.extGetRoom(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col
            );
            BCEvents.BCEvent memory currentEvent = bcEvents.extGetRoomEvent(
                currentRoom.eventNum
            );

            require(currentEvent.permanent == true, "Event not permanent");
            // TODO: Require room has no enemies (and decide if this is for some or all)
            // TODO: Confirm this will always be default effect
            players.extApplyEffect(
                _playerId,
                _gameId,
                currentGame.mapId,
                currentPlayer.position,
                currentRoom.eventNum,
                0
            );
        } else if (_action == BCTypes.Action.USE_ITEM) {
            require(
                int8(currentPlayer.numItemsHeld) <=
                    currentPlayer.currentTraits.carry,
                "Holding too many items"
            );

            // TODO: IMPLEMENT
        } else if (_action == BCTypes.Action.DROP_ITEMS) {
            // NOTE: _ids are BCItmes.items[id]
            // TODO: Should cost an action if an enemy is present
            free = true;

            require(
                _validateItemsHeld(_ids, _playerId),
                "Trying to drop item not held"
            );

            players.decrementNumItemsHeld(_playerId, _ids.length);
            items.spawnWorldItems(
                _gameId,
                _ids,
                currentPlayer.position.row,
                currentPlayer.position.col,
                _playerId
            );
            players.updatePlayerTraits(currentPlayer.characterId, _playerId);
        } else if (_action == BCTypes.Action.PICK_ITEMS) {
            // _ids are BCItems items array ids
            // TODO: This is probably quite expensive
            BCTypes.Item[] memory worldItems = items.getWorldItems(_gameId);
            for (uint i = 0; i < worldItems.length; i++) {
                // Require items are where the player is
                require(
                    currentPlayer.position.row == worldItems[i].position.row,
                    "Player not at item"
                );
                require(
                    currentPlayer.position.col == worldItems[i].position.col,
                    "Player not at item"
                );
                // Require item is KNOW or UNKNOWN
                // This one is probably redudant assuming I move destroyed or discarded items to somewhere safe
                require(
                    worldItems[i].status == BCTypes.WorldItemStatus.ON_GROUND,
                    "Item is discarded or destroyed"
                );

                // TODO: CRITICAL -> Item must be in this game (DON'T ONLY USE _gameId, user provided value!!!)
                require(
                    worldItems[i].currentGame == _gameId,
                    "Item not in this game"
                );
            }

            // TODO: Handle minting an item if it is UNKNOWN
            // TODO: Should cost an action if an enemy is present
            free = true;
            players.incrementNumItemsHeld(_playerId, _ids.length);

            items.pickupWorldItems(_playerId, _ids, currentPlayer.owner);
            players.updatePlayerTraits(currentPlayer.characterId, _playerId);
        } else if (_action == BCTypes.Action.LEAVE_GAME) {
            uint roomId = maps.extGetRoomId(
                currentGame.mapId,
                currentPlayer.position.row,
                currentPlayer.position.col
            );
            require(roomId == START_ROOM_ID, "Not in Donghaijiu");

            games.leaveGame(
                _gameId,
                _playerId,
                currentPlayer.characterId,
                currentGame.currentPlayerTurnIndex
            );
        } else if (_action == BCTypes.Action.MELEE_ATTACK) {
            games.playerAttack(
                _gameId,
                _ids[0],
                _playerId,
                uint(uint8(currentPlayer.currentTraits.melee)),
                currentPlayer.position
            );
        } else if (_action == BCTypes.Action.SHOOT_ATTACK) {
            games.playerAttack(
                _gameId,
                _ids[0],
                _playerId,
                uint(uint8(currentPlayer.currentTraits.shoot)),
                currentPlayer.position
            );
        } else {
            revert("ERROR: Bad action config");
        }

        games.endPlayerAction(_gameId, _playerId, free);
        emit ActionCompleteEvent(
            _gameId,
            currentGame,
            _playerId,
            currentPlayer,
            _action
        );
        gameToEventBlocks[_gameId].add(block.number);
    }

    // Move player to 0,0 for various mechanic tests
    function debugMovePlayerForTesting(
        uint _gameId,
        uint _playerId
    ) public onlyOwner {
        games.extMovePlayer(_gameId, BCTypes.Position(0, 0), _playerId);
    }

    function debugMovePlayerToRoom(
        uint _gameId,
        uint _playerId,
        uint _row,
        uint _col
    ) public onlyOwner {
        games.extMovePlayer(
            _gameId,
            BCTypes.Position(uint8(_row), uint8(_col)),
            _playerId
        );
    }
}
","keccak256":"0xb3b4f4571151a8ea3e51218aa9949399eefaa78405ecfd7500b27cd90e80008a","license":"UNLICENSED"},"contracts/BCEvents.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import "hardhat/console.sol";
