pragma solidity ^0.8.12;

import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "./BCTypes.sol";

// Stores information for events that happen in a specific room or from a card

contract BCEvents is Ownable {

    enum EnemyPlacement { NONE, IN_ROOM, LAST_ROOM, ALL_ADJACENT }
    enum MovePlayer { NONE, HOME, LAST_ROOM, RANDOM, PORTAL }

    // ENEMY Types: 0 - turret, 1 - robot, 2 - scav, 3 - bug

    struct BCEffect {
        bool permanant; // TODO: May be able to infer from having trait changes
        bool fullHealth;
        bool instantDeath;
        bool placeHazard;
        bool grantEgg;

        uint8 healAmt;
        uint8 healArmorAmt;
        uint8 hazardDamage;
        uint8 physicalDamage;

        uint8 placeEnemyOfType;
        uint8 numEnemyToPlace;  // TODO: Limits to only placing one type per event
        EnemyPlacement wheretoPlace;

        uint8 grantData;
        uint8 grantNumItems;
        uint8 takeNumItems;

        MovePlayer moveType;
        uint8 trapPlayerEscapeRoll;  // >0 to trap player
        uint8 grantAbility;  // TODO

        BCTypes.Traits traitIncreases; // TODO:  Should this split from one off effects to avoid waste
        BCTypes.Traits traitDecreases;
    }

    struct BCEvent {
        // TODO: Only a one-time cost to put this here, probably leave for ease
        string name;
        string text;
        bool permanant; // Draw card before use allowed.  As an action - both can be derived from this
        uint8 rollForLow;  // On or below (unused if zero)
        uint8 rollForHigh; // On or above (nothing happens for rest of numbers)

        BCEffect defaultEffect;
        BCEffect lowEffect;
        BCEffect highEffect;
    }

    BCEvent[] public bcRoomEvents;
    BCEvent[] public bcCardEvents;

    constructor() {
        _initializeDefaultRoomEvents();
    }

    function _initializeDefaultRoomEvents() internal {
        BCTypes.Traits memory noTraitsChanged;
        BCEffect memory emptyEffect;

        // TODO: Should this be some kind of script that instead makes these after deployment
        // 0 No effect
        bcRoomEvents.push(BCEvent(
            "No Event",
            "Unused",
            false,
            0,
            0,

            emptyEffect,
            emptyEffect,
            emptyEffect
        ));

        // 1 Breached Reactor
        bcRoomEvents.push(BCEvent(
            "Breached Reactor",
            "As you enter the room, the reactor explodes!  Take 1 hazard damage.",
            false,
            0,
            0, // TODO:  Explore adding choices to these ones
            BCEffect(
                false,
                false,
                false,
                true,
                false,

                0,
                0,
                1,
                0,

                0,
                0,
                EnemyPlacement.NONE,

                0,
                0,
                0,

                MovePlayer.NONE,
                0,
                0,
                noTraitsChanged,
                noTraitsChanged
            ),
            emptyEffect,
            emptyEffect
        ));

        // 2 Bug Nest
        bcRoomEvents.push(BCEvent(
            "Bug Nest",
            "As an action, attempt to harvest an egg.  Roll a die.  On a 6, take an egg token.  On a 1 or 2, take 1 physical damage and place a bug alien in the room.",
            true,  // TODO: THIS IS SCARY!!! CHAD WITH A LUCKY MAP COULD GET UNLIMITED!!!
            2,
            6,
            emptyEffect,
            BCEffect(
                false,
                false,
                false,
                false,
                false,

                0,
                0,
                0,
                1,

                3,
                1,
                EnemyPlacement.IN_ROOM,

                0,
                0,
                0,

                MovePlayer.NONE,
                0,
                0,

                noTraitsChanged,
                noTraitsChanged
            ),
            BCEffect(
                false,
                false,
                false,
                false,
                true,

                0,
                0,
                0,
                0,

                0,
                0,
                EnemyPlacement.NONE,

                0,
                0,
                0,

                MovePlayer.NONE,
                0,
                0,

                noTraitsChanged,
                noTraitsChanged
            )
        ));

        // 3 Crossroads
        bcRoomEvents.push(BCEvent(
            "Crossroads",
            "You've crashed a party!  Place 3 scavengers in this room.",
            false,
            0,
            0,

            BCEffect(
                false,
                false,
                false,
                false,
                false,

                0,
                0,
                0,
                0,

                2,
                3,
                EnemyPlacement.IN_ROOM,

                0,
                0,
                0,

                MovePlayer.NONE,
                0,
                0,

                noTraitsChanged,
                noTraitsChanged
            ),
            emptyEffect,
            emptyEffect
        ));
    }
}
