        denizenHealth[BCTypes.DenizenType.BUTCHER] = 50;
        denizenHealth[BCTypes.DenizenType.BEHEMOTH] = 200;
        denizenHealth[BCTypes.DenizenType.DOCTOR] = 25;

        denizenDef[BCTypes.DenizenType.BUG] = 3;
        denizenDef[BCTypes.DenizenType.SCAV] = 3;
